import React, { useEffect, useRef } from 'react';
import './LoadingAnimation.css'; // Import CSS for styling

const LoadingAnimation = () => {
    const erasRef = useRef([]);

    useEffect(() => {
        let currentEra = 0;

        const animateEras = () => {
            // Store the current erasRef in a local variable
            const currentEras = erasRef.current;
            // Fade in the current era
            const currentElement = currentEras[currentEra];
            if (currentElement) {
                currentElement.style.opacity = '1';

                // Set a timeout to fade out the current era
                setTimeout(() => {
                    currentElement.style.opacity = '0';

                    // Move to the next era
                    currentEra = (currentEra + 1) % currentEras.length;

                    // Start the animation again after fading out
                    setTimeout(animateEras, 500); // Wait for fade out before next animation
                }, 2000); // Duration of showing each era
            }
        };

        animateEras(); // Start the animation

        // Cleanup function
        return () => {
            // Reset opacity on unmount
            erasRef.current.forEach(era => {
                era.style.opacity = '0';
            });
        };
    }, []); // Add an empty dependency array to run effect once

    return (
        <div className="loading-container">
            <div className="era" ref={el => (erasRef.current[0] = el)}>
                <span role="img" aria-label="Prehistoric Era">ü¶ñ</span> Prehistoric Era
            </div>
            <div className="era" ref={el => (erasRef.current[1] = el)}>
                <span role="img" aria-label="Ancient Civilizations">üèõÔ∏è</span> Ancient Civilizations
            </div>
            <div className="era" ref={el => (erasRef.current[2] = el)}>
                <span role="img" aria-label="Middle Ages">üõ°Ô∏è</span> Middle Ages
            </div>
            <div className="era" ref={el => (erasRef.current[3] = el)}>
                <span role="img" aria-label="Renaissance">üé®</span> Renaissance
            </div>
            <div className="era" ref={el => (erasRef.current[4] = el)}>
                <span role="img" aria-label="Industrial Revolution">üöÇ</span> Industrial Revolution
            </div>
            <div className="era" ref={el => (erasRef.current[5] = el)}>
                <span role="img" aria-label="Modern Era">üíª</span> Modern Era
            </div>
        </div>
    );
};

export default LoadingAnimation;
