import React, { useState } from 'react';
import './PopupCard.css'; // Add some custom CSS for styling

const PopupCard = ({ title, description, imgUrl, detailedPageUrl }) => {
    const [isVisible, setIsVisible] = useState(false);

    const handleClick = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div>
            {/* Trigger button or map marker */}
            <button onClick={handleClick}>Show Info</button>

            {/* Popup card */}
            {isVisible && (
                <div className="popup-card">
                    <img src={imgUrl} alt={title} className="card-image" />
                    <h3>{title}</h3>
                    <p>{description}</p>
                    <a href={detailedPageUrl} target="_blank" rel="noopener noreferrer">More details</a>
                    <button onClick={handleClick}>Close</button>
                </div>
            )}
        </div>
    );
};

export default PopupCard;
