import React, { useState } from 'react';
import './NavBar.css';
import { FaGlobe, FaSearch, FaBars, FaArrowLeft, FaTimes } from 'react-icons/fa';

const NavBar = () => {
    const [searchVisible, setSearchVisible] = useState(false);
    const [menuVisible, setMenuVisible] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    const toggleSearch = (event) => {
        event.stopPropagation(); // Prevent event from bubbling up
        setSearchVisible(!searchVisible);
        setSearchQuery(''); // Clear the search query when closing
    };

    const toggleMenu = (event) => {
        event.stopPropagation(); // Prevent event from bubbling up
        setMenuVisible(!menuVisible);
    };

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    return (
        <>
            <nav className="navbar">
                <div className="nav-container">
                    <a href="/new-page" className="nav-logo">
                        <FaGlobe className="icon" />
                    </a>
                    <div className="nav-search" onClick={toggleSearch}>
                        <FaSearch className="icon" />
                    </div>
                    <div className="nav-menu" onClick={toggleMenu}>
                        <FaBars className="icon" />
                    </div>
                </div>
            </nav>

            {/* Search Overlay */}
            {searchVisible && (
                <div className="search-overlay" onClick={() => setSearchVisible(false)}>
                    <div className="back-button" onClick={toggleSearch}>
                        <FaArrowLeft className="icon" />
                    </div>
                    <div className="search-box">
                        <input
                            type="text"
                            className="search-input"
                            placeholder="Search..."
                            value={searchQuery}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="overlay-close" onClick={() => setSearchVisible(false)}></div>
                </div>
            )}

            {/* Menu Overlay */}
            {menuVisible && (
                <div className="menu-overlay" onClick={() => setMenuVisible(false)}>
                    <div className="menu-close" onClick={toggleMenu}>
                        <FaTimes className="icon" />
                    </div>
                    <div className="menu-options">
                        <ul>
                            <li><a href="#map">MAP</a></li>
                            <li><a href="#historical-figures">Historical Figures</a></li>
                            <li><a href="#world-war-1">World War 1</a></li>
                            <li><a href="#world-war-2">World War 2</a></li>
                            <li><a href="#dinosaur-era">The Dinosaur Era (Mesozoic Era)</a></li>
                            {/* Add more options here */}
                        </ul>
                    </div>
                </div>
            )}
        </>
    );
};

export default NavBar;
