import React, { useEffect, useRef, useState } from 'react';
import Lottie from 'lottie-react';
import muteUnmuteAnimation from './mute_unmute.json'; // Adjust the path as necessary
import './LoadingPage.css';

const LoadingPage = () => {
    const videoRef = useRef(null);
    const audioRef = useRef(null);
    const [displayedMessage, setDisplayedMessage] = useState('');
    const intervalRef = useRef(null);
    const [isMuted, setIsMuted] = useState(true);

    const fullMessage = "Step Back in Time: Explore History with Us!";

    useEffect(() => {
        if (videoRef.current) {
            videoRef.current.playbackRate = 0.5;
        }

        // Function to display the message one letter at a time
        const displayMessage = (message) => {
            let index = 0;
            setDisplayedMessage('');

            intervalRef.current = setInterval(() => {
                if (index < message.length) {
                    setDisplayedMessage((prev) => prev + message[index]);
                    index++;
                } else {
                    clearInterval(intervalRef.current);
                }
            }, 150);
        };

        // Start displaying the message
        displayMessage(fullMessage);

        // Cleanup the interval on component unmount
        return () => clearInterval(intervalRef.current);
    }, []);

    const handleMuteToggle = () => {
        setIsMuted((prev) => !prev);
        if (audioRef.current) {
            if (isMuted) {
                audioRef.current.play(); // Play audio
            } else {
                audioRef.current.pause(); // Pause audio
            }
        }
    };

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted={isMuted} // Set mute based on state
                className="video-background"
            >
                <source src="/loadingBG2.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <audio ref={audioRef} loop>
                <source src="/landing_speech.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            <div className="welcome-message">
                {displayedMessage}
            </div>

            <div className="logo-container">
                <img src="/logo1.png" alt="Time Machine Logo" className="logo" />
            </div>

            <div className="mute-icon" onClick={handleMuteToggle}>
                <Lottie 
                    animationData={muteUnmuteAnimation} 
                    loop={false} 
                    play 
                    style={{ width: 50, height: 50 }} 
                />
            </div>
        </div>
    );
};

export default LoadingPage;
