// YearInfo.js
import React from 'react';
import './YearInfo.css'; // Import the CSS file

const YearInfo = () => {
  // Hardcoded data for demonstration
  const country = "India";
  const year = 1600;

  // Significant events in 1600
  const significantEvents = [
    {
      title: "Formation of the British East India Company",
      description: "Incorporated by royal charter on December 31, 1600, the East India Company was formed for the exploitation of trade with East and Southeast Asia and India. Initially, it served as a monopolistic trading body but later became involved in politics, acting as an agent of British imperialism in India from the early 18th century to the mid-19th century.",
      details: "The company aimed to share in the East Indian spice trade, which had been a monopoly of Spain and Portugal until England's victory over the Spanish Armada in 1588. The company faced opposition from the Dutch in the Dutch East Indies and the Portuguese. Notably, after the Amboina Massacre in 1623, the Dutch effectively excluded company members from the East Indies. However, the company's defeat of the Portuguese in India in 1612 allowed it to gain trading concessions from the Mughal Empire. The company focused on trading cotton and silk goods, indigo, and saltpetre, as well as spices from South India. It also expanded its operations to the Persian Gulf, Southeast Asia, and East Asia."
    },
    // Add more significant events as needed
    {
      title: "Opposition from the Dutch",
      description: "The East India Company faced significant challenges from the Dutch, particularly in the Dutch East Indies, impacting its trading operations."
    }
  ];

  // Traditional knowledge and scientific achievements
  const traditionalKnowledge = [
    {
      title: "Traditional Indian Knowledge in Textiles and Metallurgy",
      description: "During this time, traditional Indian knowledge in textiles and metallurgy continued to thrive. Indian artisans were renowned for their skills in weaving and metalwork, significantly contributing to both local economies and global trade."
    }
  ];

  const scientificAchievements = [
    {
      title: "Advancements in Textiles",
      description: "India was known for its high-quality textiles, including cotton and silk. Innovations in dyeing and weaving techniques made Indian fabrics highly sought after in global markets."
    },
    {
      title: "Metallurgical Innovations",
      description: "Indian metallurgists made significant advancements, particularly in the production of high-quality steel, such as 'Wootz steel.' This innovation would influence metal production techniques worldwide."
    }
  ];

  return (
    <div className="container">
      <h2>{country} - {year}</h2>

      <div className="section">
        <h3>Significant Events</h3>
        {significantEvents.map((event, index) => (
          <div key={index} className="event">
            <h4>{event.title}</h4>
            <p>{event.description}</p>
            {event.details && <p className="details">{event.details}</p>} {/* Detailed info */}
          </div>
        ))}
      </div>

      <div className="section">
        <h3>Traditional Knowledge</h3>
        {traditionalKnowledge.map((knowledge, index) => (
          <div key={index} className="knowledge">
            <h4>{knowledge.title}</h4>
            <p>{knowledge.description}</p>
          </div>
        ))}
      </div>

      <div className="section">
        <h3>Scientific Achievements</h3>
        {scientificAchievements.map((achievement, index) => (
          <div key={index} className="achievement">
            <h4>{achievement.title}</h4>
            <p>{achievement.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YearInfo;
