// YearInfo.js
import React from 'react';
import './YearInfo.css'; // Import the CSS file

const YearInfo = () => {
  // Hardcoded data for demonstration
  const country = "India";
  const year = 1600;

  // Significant events in 1600
  const significantEvents = [
    {
      title: "Formation of the British East India Company",
      shortDescription: "Incorporated by royal charter on December 31, 1600, the East India Company was formed for the exploitation of trade with East and Southeast Asia and India.",
      details: "East India Company, an English company formed for the exploitation of trade with East and Southeast Asia and India, was incorporated by royal charter on December 31, 1600. Starting as a monopolistic trading body, the company became involved in politics and acted as an agent of British imperialism in India from the early 18th century to the mid-19th century. The company was formed to share in the East Indian spice trade. That trade had been a monopoly of Spain and Portugal until the defeat of the Spanish Armada (1588) by England gave the English the chance to break the monopoly. The company met with opposition from the Dutch in the Dutch East Indies (now Indonesia) and the Portuguese. The Dutch virtually excluded company members from the East Indies after the Amboina Massacre in 1623 (an incident in which English, Japanese, and Portuguese traders were executed by Dutch authorities), but the company’s defeat of the Portuguese in India (1612) won them trading concessions from the Mughal Empire. The company settled down to a trade in cotton and silk piece goods, indigo, and saltpetre, with spices from South India. It extended its activities to the Persian Gulf, Southeast Asia, and East Asia."
    },
    // Additional events can be added here
  ];

  // Scientific achievements
  const scientificAchievements = [
    {
      title: "Advancements in Textiles",
      description: "Indian textiles were characterized by their intricate designs and high-quality craftsmanship, with techniques that produced vibrant colors and patterns. The use of natural dyes and innovative weaving methods set Indian textiles apart in the global market.",
      details: "Innovations such as the use of resist dyeing and block printing allowed for the creation of unique patterns, enhancing the aesthetic appeal of fabrics. The silk production process was particularly advanced, with Indian silk being highly prized across Europe and Asia."
    },
    {
      title: "Metallurgical Innovations",
      description: "Indian metallurgists made significant advancements, particularly in the production of high-quality steel, such as 'Wootz steel.' This innovation would influence metal production techniques worldwide."
    }
  ];

  return (
    <div className="container">
      <h2>{country} - {year}</h2>

      <div className="section">
        <h3>Significant Events</h3>
        {significantEvents.map((event, index) => (
          <div key={index} className="event">
            <h4>{event.title}</h4>
            <p>{event.shortDescription}</p>
            <p className="details">{event.details}</p> {/* Detailed info */}
          </div>
        ))}
      </div>

      <div className="section">
        <h3>Scientific Achievements</h3>
        {scientificAchievements.map((achievement, index) => (
          <div key={index} className="achievement">
            <h4>{achievement.title}</h4>
            <p>{achievement.description}</p>
            <p className="details">{achievement.details}</p> {/* Details */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default YearInfo;
