import React, { useState } from 'react';
import './NavBar.css';
import { FaGlobe, FaSearch, FaBars, FaArrowLeft } from 'react-icons/fa';

const NavBar = () => {
    const [searchVisible, setSearchVisible] = useState(false);
    const [menuVisible, setMenuVisible] = useState(false); // State for menu visibility
    const [searchQuery, setSearchQuery] = useState('');

    // Toggle search overlay visibility
    const toggleSearch = () => {
        setSearchVisible(!searchVisible);
        setSearchQuery(''); // Clear the search query when closing
    };

    // Toggle menu visibility
    const toggleMenu = () => {
        setMenuVisible(!menuVisible);
    };

    // Handle input change for the search bar
    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    return (
        <>
            <nav className="navbar">
                <div className="nav-container">
                    {/* Left: Globe Icon */}
                    <a href="/new-page" className="nav-logo">
                        <span role="img" aria-label="Globe Icon">
                            <FaGlobe className="icon" />
                        </span>
                    </a>

                    {/* Middle: Search Icon */}
                    <div className="nav-search" onClick={toggleSearch}>
                        <span role="img" aria-label="Search Icon">
                            <FaSearch className="icon" />
                        </span>
                    </div>

                    {/* Right: Menu Icon */}
                    <div className="nav-menu" onClick={toggleMenu}>
                        <span role="img" aria-label="Menu Icon">
                            <FaBars className="icon" />
                        </span>
                    </div>
                </div>
            </nav>

            // Existing imports and code...

            {/* Sliding Menu */}
            <div className={`slide-in-menu ${menuVisible ? 'active' : ''}`}>
                <div className="menu-content">
                    <h2>Menu</h2>
                    <ul>
                        <li><a href="#home">Home</a></li>
                        <li><a href="#about">About</a></li>
                        <li><a href="#services">Services</a></li>
                        <li><a href="#contact">Contact</a></li>
                    </ul>
                </div>
            </div>

// Existing code...


            {/* Search Overlay */}
            {searchVisible && (
                <div className="search-overlay">
                    {/* Back Button */}
                    <div className="back-button" onClick={toggleSearch}>
                        <span role="img" aria-label="Back Button">
                            <FaArrowLeft className="icon" />
                        </span>
                    </div>

                    {/* Search Box */}
                    <div className="search-box">
                        <input
                            type="text"
                            className="search-input"
                            placeholder="Search..."
                            value={searchQuery}   // Bind the input value to searchQuery state
                            onChange={handleInputChange}  // Handle typing
                        />
                    </div>

                    <div className="overlay-close" onClick={toggleSearch}></div>
                </div>
            )}
        </>
    );
};

export default NavBar;
