import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HistoricalFigures.css';

const HistoricalFigures = () => {
    const [figures, setFigures] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchHistoricalFigures = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/historical-figures');
                setFigures(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching historical figures:', error);
                setLoading(false);
            }
        };

        fetchHistoricalFigures();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="historical-figures-container">
            <h1 style={{ color: 'white' }}>Historical Figures</h1>
            <div className="card-container">
                {figures.map(figure => (
                    <div key={figure.id} className="card">
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HistoricalFigures;
