import React, { useState } from 'react';
import Globe from 'react-globe.gl';
import PopupCard from './PopupCard'; // Import the PopupCard component

const GlobalMap = () => {
    const [selectedPoint, setSelectedPoint] = useState(null);

    const points = [
        { id: 1, lat: 51.505, lng: -0.09, title: 'Point 1', description: 'Description of point 1', imgUrl: 'img1.jpg', detailedPageUrl: '/point1' },
        { id: 2, lat: 40.7128, lng: -74.0060, title: 'Point 2', description: 'Description of point 2', imgUrl: 'img2.jpg', detailedPageUrl: '/point2' },
        { id: 3, lat: 35.6762, lng: 139.6503, title: 'Point 3', description: 'Description of point 3', imgUrl: 'img3.jpg', detailedPageUrl: '/point3' },
    ];

    return (
        <div>
            <Globe
                globeImageUrl="//unpkg.com/three-globe/example/img/earth-night.jpg"
                pointsData={points}
                pointAltitude={0.1}
                pointColor={() => 'red'}
                onPointClick={(point) => setSelectedPoint(point)}
                pointLat="lat"
                pointLng="lng"
            />

            {/* Show the popup card for the selected point */}
            {selectedPoint && (
                <PopupCard
                    title={selectedPoint.title}
                    description={selectedPoint.description}
                    imgUrl={selectedPoint.imgUrl}
                    detailedPageUrl={selectedPoint.detailedPageUrl}
                />
            )}
        </div>
    );
};

export default GlobalMap;
