import React, { useEffect, useRef, useState } from 'react';
import './LoadingPage.css'; // Import your CSS for styling

const LoadingPage = () => {
    const videoRef = useRef(null);
    const audioRef = useRef(null);
    const [displayedMessage, setDisplayedMessage] = useState('');
    const intervalRef = useRef(null);

    const fullMessage = "Step Back in Time: Explore History with Us!";

    useEffect(() => {
        // Set video playback rate
        if (videoRef.current) {
            videoRef.current.playbackRate = 0.5;
        }

        // Try to play audio
        const playAudio = async () => {
            if (audioRef.current) {
                try {
                    await audioRef.current.play();
                    console.log("Audio is playing");
                } catch (error) {
                    console.error("Error playing audio:", error);
                }
            }
        };

        // Start playing audio
        playAudio();

        // Function to display the message one letter at a time
        const displayMessage = (message) => {
            let index = 0;
            setDisplayedMessage('');

            intervalRef.current = setInterval(() => {
                if (index < message.length) {
                    setDisplayedMessage((prev) => prev + message[index]);
                    index++;
                } else {
                    clearInterval(intervalRef.current);
                }
            }, 150);
        };

        // Start displaying the message
        displayMessage(fullMessage);

        // Cleanup the interval on component unmount
        return () => clearInterval(intervalRef.current);
    }, []);

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted
                className="video-background"
            >
                <source src="/loadingBG2.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <audio ref={audioRef} autoPlay loop>
                <source src="/landing_speech.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            <div className="welcome-message">
                {displayedMessage}
            </div>

            <div className="logo-container">
                <img src="/logo1.png" alt="Time Machine Logo" className="logo" />
            </div>

            {/* Button to manually play the audio for testing */}
            <button onClick={() => {
                audioRef.current.play().then(() => {
                    console.log("Manual audio playback successful.");
                }).catch((error) => {
                    console.error("Manual audio playback failed:", error);
                });
            }}>
                Play Voice-Over
            </button>
        </div>
    );
};

export default LoadingPage;
