import React, { useEffect, useRef } from 'react';
import './LoadingAnimation.css'; // Import CSS for styling

const LoadingAnimation = () => {
    const erasRef = useRef([]);

    useEffect(() => {
        let currentEra = 0;

        const animateEras = () => {
            // Fade in the current era
            erasRef.current[currentEra].style.opacity = '1';

            // Set a timeout to fade out the current era
            setTimeout(() => {
                erasRef.current[currentEra].style.opacity = '0';

                // Move to the next era
                currentEra = (currentEra + 1) % erasRef.current.length;

                // Start the animation again after fading out
                setTimeout(animateEras, 500); // Wait for fade out before next animation
            }, 2000); // Duration of showing each era
        };

        animateEras(); // Start the animation

        // Cleanup function
        return () => {
            erasRef.current.forEach(era => {
                era.style.opacity = '0'; // Reset opacity on unmount
            });
        };
    }, []);

    return (
        <div className="loading-container">
            <div className="era" ref={el => (erasRef.current[0] = el)}>ü¶ñ Prehistoric Era</div>
            <div className="era" ref={el => (erasRef.current[1] = el)}>üèõÔ∏è Ancient Civilizations</div>
            <div className="era" ref={el => (erasRef.current[2] = el)}>üõ°Ô∏è Middle Ages</div>
            <div className="era" ref={el => (erasRef.current[3] = el)}>üé® Renaissance</div>
            <div className="era" ref={el => (erasRef.current[4] = el)}>üöÇ Industrial Revolution</div>
            <div className="era" ref={el => (erasRef.current[5] = el)}>üíª Modern Era</div>
        </div>
    );
};

export default LoadingAnimation;
