import React, { useEffect, useState, useRef } from 'react';
import './HistoricalFigures.css'; // Ensure you have a CSS file for styling

const HistoricalFigures = () => {
    const [figures, setFigures] = useState([]);
    const [activeIndex, setActiveIndex] = useState(1);
    const [hoverIndex, setHoverIndex] = useState(null);
    const [scrollInterval, setScrollInterval] = useState(null);
    const sliderRef = useRef(null); // Reference to the slider element

    useEffect(() => {
        const fetchFigures = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/historical-figures');
                const data = await response.json();
                setFigures(data);
            } catch (error) {
                console.error('Error fetching historical figures:', error);
            }
        };

        fetchFigures();
    }, []);

    const autoScroll = (direction) => {
        const container = document.querySelector('.figures-cards');
        const scrollAmount = direction === 'left' ? -1 : 1;

        const scrollStep = () => {
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
        };

        setScrollInterval(setInterval(scrollStep, 16));
    };

    const handleCardClick = (index) => {
        setActiveIndex(index);
    };

    const handleCardHover = (index) => {
        setHoverIndex(index);
        const audio = new Audio(`${process.env.PUBLIC_URL}/assets/sounds/click.mp3`);
        audio.play().catch((error) => {
            console.error('Failed to play audio:', error);
        });
    };

    const handleCardHoverLeave = () => {
        setHoverIndex(null);
        clearInterval(scrollInterval);
        setScrollInterval(null);
    };

    const handleMouseMove = (e) => {
        const container = document.querySelector('.figures-cards');
        const { left, right } = container.getBoundingClientRect();

        if (e.clientX < left + 5) {
            autoScroll('left');
        } else if (e.clientX > right - 5) {
            autoScroll('right');
        } else {
            clearInterval(scrollInterval);
            setScrollInterval(null);
        }
    };

    const handleDrag = (e) => {
        const slider = sliderRef.current;
        const sliderWidth = slider.clientWidth;
        const totalFigures = figures.length;

        // Calculate the new position
        let newLeft = e.clientX - slider.getBoundingClientRect().left;
        if (newLeft < 0) newLeft = 0;
        if (newLeft > sliderWidth) newLeft = sliderWidth;

        // Set draggable position
        const draggable = slider.querySelector('.draggable');
        draggable.style.left = `${newLeft - draggable.offsetWidth / 2}px`; // Center the draggable

        // Calculate the scroll percentage
        const scrollPercentage = (newLeft / sliderWidth) * (totalFigures - 1);
        const targetIndex = Math.round(scrollPercentage);

        // Scroll to the target index
        const container = document.querySelector('.figures-cards');
        const cardWidth = container.scrollWidth / totalFigures; // Width of each card
        container.scrollTo({ left: targetIndex * cardWidth, behavior: 'smooth' });
    };

    return (
        <div className="historical-figures-container" onMouseMove={handleMouseMove}>
            {/* Video Background */}
            <video autoPlay loop muted playsInline className="background-video">
                <source src={`${process.env.PUBLIC_URL}/historyBG.mp4`} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div className="video-overlay"></div>

            {/* Main Content */}
            <h1 style={{ color: 'Black' }}>Historical Figures</h1>
            <div className="figures-cards">
                {figures.map((figure, index) => (
                    <div
                        key={figure.id}
                        className={`figure-card ${activeIndex === index ? 'active' : ''} ${hoverIndex === index ? 'hovered' : ''}`}
                        onClick={() => handleCardClick(index)}
                        onMouseEnter={() => handleCardHover(index)}
                        onMouseLeave={handleCardHoverLeave}
                    >
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                        <p><strong>Born:</strong> {new Date(figure.birth_date).toLocaleDateString()}</p>
                        <p><strong>Died:</strong> {new Date(figure.death_date).toLocaleDateString()}</p>
                        <div className="content">
                            <p>{figure.additionalInfo}</p>
                        </div>
                    </div>
                ))}
            </div>

           {/* Draggable Slider */}
           <div className="slider-container">
                <div className="slider" ref={sliderRef} onMouseMove={handleDrag}>
                    <div className="draggable"></div>
                </div>

            </div>
        </div>
    );
};

export default HistoricalFigures;
