import React, { useState, useEffect } from 'react';
import GlobalMap from './comp/GlobalMap';
import LoadingAnimation from './comp/LoadingAnimation'; // Import the Loading Animation
import './App.css'; // Optional: Add general styling for the app

const App = () => {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate loading for 5 seconds (replace with your actual loading logic)
        const loadingTimer = setTimeout(() => {
            setLoading(false);
        }, 5000); // Adjust the time as needed

        // Cleanup the timer on component unmount
        return () => clearTimeout(loadingTimer);
    }, []);

    return (
        <div className="App">
            {loading ? (
                <LoadingAnimation /> // Show the loading animation while loading
            ) : (
                <>
                    <h1>Interactive Map with Popup Cards</h1>
                    <GlobalMap />
                </>
            )}
        </div>
    );
};

export default App;
