import React, { useState } from 'react';
import PopupCard from './PopupCard'; // Import the PopupCard component
import './GlobalMap.css'; // Add CSS for map points and image styling

const GlobalMap = () => {
    const [selectedPoint, setSelectedPoint] = useState(null);

    // Define clickable points with coordinates relative to the SVG
    const points = [
        { id: 1, title: 'Point 1', description: 'Description of point 1', imgUrl: 'img1.jpg', detailedPageUrl: '/point1', top: '20%', left: '40%' },
        { id: 2, title: 'Point 2', description: 'Description of point 2', imgUrl: 'img2.jpg', detailedPageUrl: '/point2', top: '50%', left: '60%' },
        { id: 3, title: 'Point 3', description: 'Description of point 3', imgUrl: 'img3.jpg', detailedPageUrl: '/point3', top: '70%', left: '30%' },
    ];

    const handlePointClick = (point) => {
        setSelectedPoint(point);
    };

    return (
        <div className="global-map">
            {/* Use the SVG image */}
            <img src="/path-to-your-world.svg" alt="World Map" className="map-image" />

            {/* Display points on the static map */}
            {points.map((point) => (
                <div
                    key={point.id}
                    className="map-point"
                    style={{ top: point.top, left: point.left }}
                    onClick={() => handlePointClick(point)}
                >
                    â€¢
                </div>
            ))}

            {/* Show the popup card for the selected point */}
            {selectedPoint && (
                <PopupCard
                    title={selectedPoint.title}
                    description={selectedPoint.description}
                    imgUrl={selectedPoint.imgUrl}
                    detailedPageUrl={selectedPoint.detailedPageUrl}
                />
            )}
        </div>
    );
};

export default GlobalMap;
