// HistoricalFigures.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HistoricalFigures.css'; // Add your CSS file for styling

const HistoricalFigures = () => {
    const [figures, setFigures] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFigures = async () => {
            try {
                const response = await axios.get('/api/historical-figures'); // Adjust the API endpoint if needed
                setFigures(response.data);
            } catch (error) {
                console.error('Error fetching historical figures:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFigures();
    }, []);

    if (loading) {
        return <div>Loading...</div>; // Loading state
    }

    return (
        <div className="historical-figures-container">
            <h1>Historical Figures</h1>
            <div className="figures-cards">
                {figures.map((figure) => (
                    <div className="figure-card" key={figure.id}>
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                        {/* Add more figure details as needed */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HistoricalFigures;
