import React, { useEffect, useState } from 'react';
import './HistoricalFigures.css';

const HistoricalFigures = () => {
    const [figures, setFigures] = useState([]);
    const [activeIndex, setActiveIndex] = useState(0); // Start with the first card as active

    // Fetch figures and simulate more data for testing
    useEffect(() => {
        const fetchFigures = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/historical-figures');
                const data = await response.json();
                setFigures(data);
            } catch (error) {
                console.error('Error fetching historical figures:', error);
            }
        };

        fetchFigures();
    }, []);

    // Slide to the next card every 5 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            setActiveIndex((prevIndex) => (prevIndex + 1) % figures.length);
        }, 5000);

        return () => clearInterval(interval);
    }, [figures.length]);

    // Adjust card display for a dynamic number of figures
    const handleCardClick = (index) => {
        setActiveIndex(index); // Update active card
    };

    return (
        <div className="historical-figures-container">
            <h1>Historical Figures</h1>
            <div className="figures-cards">
                {figures.map((figure, index) => (
                    <div
                        key={figure.id}
                        className={`figure-card ${activeIndex === index ? 'active' : ''}`}
                        onClick={() => handleCardClick(index)}
                    >
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                        <p><strong>Born:</strong> {new Date(figure.birth_date).toLocaleDateString()}</p>
                        <p><strong>Died:</strong> {new Date(figure.death_date).toLocaleDateString()}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HistoricalFigures;
