import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HistoricalFiguresCardPage.css'; // Ensure you have CSS for styling

const HistoricalFiguresCardPage = () => {
    const [figures, setFigures] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/api/historical-figures')
            .then(response => setFigures(response.data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    return (
        <div className="figures-container">
            <div className="arrow left">{'<'}</div> {/* Left arrow */}
            <div className="cards-wrapper">
                {figures.map((figure, index) => (
                    <div key={index} className="card">
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                        <img src={figure.imageUrl} alt={figure.name} />
                    </div>
                ))}
            </div>
            <div className="arrow right">{'>'}</div> {/* Right arrow */}
        </div>
    );
};

export default HistoricalFiguresCardPage;
