import React, { useEffect, useRef } from 'react';
import './LoadingPage.css'; // Import your CSS for styling

const LoadingPage = () => {
    const videoRef = useRef(null);

    useEffect(() => {
        if (videoRef.current) {
            // Set the playback speed to slow down the video
            videoRef.current.playbackRate = 0.5; // Adjust this value for slower or faster playback (0.5 is 50% speed)
        }
    }, []);

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted
                style={{
                    position: 'absolute',
                    top: '0',
                    left: '0',
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover', // This ensures the video covers the entire background while maintaining its aspect ratio
                    zIndex: -1 // Make sure the video stays behind other content
                }}
            >
                <source src="/landingBG1.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        </div>
    );
};

export default LoadingPage;
