import React, { useEffect, useState } from 'react';
import './HistoricalFigures.css'; // Ensure you have a CSS file for styling

const HistoricalFigures = () => {
    const [figures, setFigures] = useState([]);
    const [activeIndex, setActiveIndex] = useState(1); // Default to the middle card
    const [hoverIndex, setHoverIndex] = useState(null); // Track the card being hovered over

    useEffect(() => {
        const fetchFigures = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/historical-figures');
                const data = await response.json();
                setFigures(data);
            } catch (error) {
                console.error('Error fetching historical figures:', error);
            }
        };

        fetchFigures();
    }, []);

    const handleCardClick = (index) => {
        setActiveIndex(index);
    };

    const handleCardHover = (index) => {
        setHoverIndex(index);
    };

    const handleCardHoverLeave = () => {
        setHoverIndex(null);
    };

    return (
        <div className="historical-figures-container">
            <h1 style={{ color: 'white' }}>Historical Figures</h1>
            <div className="figures-cards">
                {figures.map((figure, index) => (
                    <div
                        key={figure.id}
                        className={`figure-card ${activeIndex === index ? 'active' : ''} ${hoverIndex === index ? 'hovered' : ''}`}
                        onClick={() => handleCardClick(index)} // Click to set active card
                        onMouseEnter={() => handleCardHover(index)} // Hover to apply hover effect
                        onMouseLeave={handleCardHoverLeave} // Remove hover effect when leaving
                    >
                        <h2>{figure.name}</h2>
                        <p>{figure.description}</p>
                        <p><strong>Born:</strong> {new Date(figure.birth_date).toLocaleDateString()}</p>
                        <p><strong>Died:</strong> {new Date(figure.death_date).toLocaleDateString()}</p>
                        <div className="content">
                            <p>{figure.additionalInfo}</p> {/* Optional additional information */}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HistoricalFigures;
