import React, { useState } from 'react';
import PopupCard from './PopupCard'; // Import the PopupCard component
import './GlobalMap.css'; // Add CSS for map points and image styling

const GlobalMap = () => {
    const [selectedPoint, setSelectedPoint] = useState(null);
    const [isTransitioning, setIsTransitioning] = useState(false); // New state for transition

    // Define clickable points with coordinates relative to the map image
    const points = [
        { id: 1, title: 'Warrior', name: 'Geralt of Rivia', description: 'A skilled Witcher and monster hunter.', imgUrl: '/profile.jpg', detailedPageUrl: '/point1', top: '20%', left: '40%' },
        { id: 2, title: 'Mage', name: 'Yennefer of Vengerberg', description: 'A powerful sorceress with a mysterious past.', imgUrl: '/img2.jpg', detailedPageUrl: '/point2', top: '50%', left: '60%' },
        { id: 3, title: 'Princess', name: 'Ciri of Cintra', description: 'A princess with a destiny tied to Geralt and Yennefer.', imgUrl: '/img3.jpg', detailedPageUrl: '/point3', top: '70%', left: '30%' },
    ];

    const handlePointClick = (point) => {
        if (selectedPoint && selectedPoint.id === point.id) {
            // If the same point is clicked, just close it
            setSelectedPoint(null);
        } else {
            // If a new point is clicked, set the new point directly
            setSelectedPoint(point);
        }
    };

    const handleClosePopup = () => {
        setSelectedPoint(null); // Close the popup
    };

    return (
        <div className="global-map">
            {/* Use the SVG image from the public folder */}
            <img src="/world.svg" alt="World Map" className="map-image" />

            {/* Display points on the static map */}
            {points.map((point) => (
                <div
                    key={point.id}
                    className="map-point"
                    style={{ top: point.top, left: point.left }}
                    onClick={() => handlePointClick(point)}
                >
                    â€¢
                </div>
            ))}

            {/* Show the popup card for the selected point */}
            {selectedPoint && (
                <PopupCard
                    title={selectedPoint.title}
                    name={selectedPoint.name}
                    description={selectedPoint.description}
                    imgUrl={selectedPoint.imgUrl}
                    detailedPageUrl={selectedPoint.detailedPageUrl}
                    onClose={handleClosePopup}
                />
            )}
        </div>
    );
};

export default GlobalMap;
