import React, { useState } from 'react';
import PopupCard from './PopupCard'; // Import the PopupCard component
import './GlobalMap.css'; // Optional: Add CSS for styling map points

const GlobalMap = () => {
    const [selectedPoint, setSelectedPoint] = useState(null);

    const points = [
        { id: 1, title: 'Point 1', description: 'Description of point 1', imgUrl: 'img1.jpg', detailedPageUrl: '/point1' },
        { id: 2, title: 'Point 2', description: 'Description of point 2', imgUrl: 'img2.jpg', detailedPageUrl: '/point2' },
        { id: 3, title: 'Point 3', description: 'Description of point 3', imgUrl: 'img3.jpg', detailedPageUrl: '/point3' },
    ];

    const handlePointClick = (point) => {
        setSelectedPoint(point);
    };

    return (
        <div className="global-map">
            {/* Display points on the map */}
            {points.map((point) => (
                <div key={point.id} className="map-point" onClick={() => handlePointClick(point)}>
                    {point.title}
                </div>
            ))}

            {/* Show the popup card for the selected point */}
            {selectedPoint && (
                <PopupCard
                    title={selectedPoint.title}
                    description={selectedPoint.description}
                    imgUrl={selectedPoint.imgUrl}
                    detailedPageUrl={selectedPoint.detailedPageUrl}
                />
            )}
        </div>
    );
};

export default GlobalMap;
