import React, { useState, useEffect } from "react";
import './HistoricalFigures.css'; // CSS file for custom styling.

const HistoricalFigures = () => {
  const [figures, setFigures] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/historical-figures');
        const data = await response.json();
        console.log("Fetched figures:", data); // Debugging line
        setFigures(data);  // Set the fetched data to state
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="historical-figures-container">
      <h1>Historical Figures</h1>
      <div className="figures-cards">
        {figures.length === 0 ? (
          <p>No historical figures found.</p>
        ) : (
          figures.map((figure) => (
            <div key={figure._id} className="figure-card">
              <img
                src={`http://localhost:3000${figure.image_path}`}  // Image URL debugging
                alt={figure.name}
                className="figure-image"
              />
              <div className="figure-info">
                <h2>{figure.name}</h2>
                <p>{figure.description}</p>
                <p><strong>Born:</strong> {figure.birth_year}</p>
                <p><strong>Died:</strong> {figure.death_year}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default HistoricalFigures;
