import React, { useEffect, useRef, useState } from 'react';
import './LoadingPage.css'; // Import your CSS for styling

const LoadingPage = () => {
    const videoRef = useRef(null);
    const audioRef = useRef(null);
    const [displayedMessage, setDisplayedMessage] = useState('');

    const fullMessage = "Step Back in Time: Explore History with Us!";
    let interval; // Move interval declaration here for scope accessibility

    useEffect(() => {
        if (videoRef.current) {
            videoRef.current.playbackRate = 0.5; // Adjust this value for slower or faster playback
        }
        if (audioRef.current) {
            audioRef.current.play();
        }

        // Function to display the message one letter at a time
        const displayMessage = (message) => {
            let index = 0;
            setDisplayedMessage(''); // Reset the message on each render

            interval = setInterval(() => { // Assign interval here
                if (index < message.length) {
                    setDisplayedMessage((prev) => prev + message[index]);
                    index++;
                } else {
                    clearInterval(interval); // Stop when the message is fully displayed
                }
            }, 100); // Adjust the speed of each letter's appearance (100ms here)
        };

        // Start displaying the message
        displayMessage(fullMessage);

        // Cleanup the interval on component unmount
        return () => clearInterval(interval);
    }, [fullMessage]);

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted
                className="video-background" // Use the updated CSS class
            >
                <source src="/landingBG1.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <audio ref={audioRef} autoPlay loop>
                <source src="/breeze-sound.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            <div className="welcome-message">
                {displayedMessage}
            </div>
        </div>
    );
};

export default LoadingPage;
