import React from 'react';
import './PopupCard.css'; // Make sure the CSS is updated

const PopupCard = ({ title, description, imgUrl, detailedPageUrl, onClose }) => {
    return (
        <div className="popup-card-container">
            {/* Dimmed background */}
            <div className="popup-overlay" onClick={onClose}></div>

            {/* Popup card */}
            <div className="popup-card">
                <div className="popup-image" style={{ backgroundImage: `url(${imgUrl})` }}>
                    {/* Optional image overlay */}
                    <div className="popup-image-overlay"></div>
                </div>
                <div className="popup-content">
                    <h3 className="popup-title">{title}</h3>
                    <p className="popup-description">{description}</p>
                    <a href={detailedPageUrl} target="_blank" rel="noopener noreferrer" className="popup-link">
                        Learn More
                    </a>
                    <button className="popup-close" onClick={onClose}>X</button>
                </div>
            </div>
        </div>
    );
};

export default PopupCard;
