import React, { useEffect, useRef, useState } from 'react';
import './LoadingPage.css'; // Import your CSS for styling

const LoadingPage = () => {
    const videoRef = useRef(null);
    const audioRef = useRef(null);
    const voiceRef = useRef(null); // Create a ref for the voice-over audio
    const [displayedMessage, setDisplayedMessage] = useState(''); // Initialize to an empty string
    const intervalRef = useRef(null); // Create a ref for the interval

    const fullMessage = "Step Back in Time: Explore History with Us!";

    useEffect(() => {
        if (videoRef.current) {
            videoRef.current.playbackRate = 0.5; // Adjust playback speed
        }
        if (audioRef.current) {
            audioRef.current.play(); // Ensure background audio plays automatically
        }

        // Function to display the message one letter at a time
        const displayMessage = (message) => {
            let index = 0; // Initialize index for letters
            setDisplayedMessage(''); // Reset the message

            intervalRef.current = setInterval(() => {
                if (index < message.length) {
                    setDisplayedMessage((prev) => prev + message[index]);
                    index++;
                } else {
                    clearInterval(intervalRef.current); // Stop when the message is fully displayed
                    if (voiceRef.current) {
                        voiceRef.current.play(); // Play voice-over after message display
                    }
                }
            }, 150); // Adjust speed here
        };

        // Start displaying the message
        displayMessage(fullMessage);

        // Cleanup the interval on component unmount
        return () => clearInterval(intervalRef.current);
    }, []); // Note: We removed fullMessage from dependencies to avoid resets

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted
                className="video-background" // Use the updated CSS class
            >
                <source src="/loadingBG2.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <audio ref={audioRef} autoPlay loop>
                <source src="/breeze-sound.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            {/* Add Voice-Over Audio */}
            <audio ref={voiceRef}>
                <source src="/landing_speech.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            <div className="welcome-message">
                {displayedMessage} {/* Only display the message */}
            </div>

            {/* Logo Positioning */}
            <div className="logo-container">
                <img src="/logo1.png" alt="Time Machine Logo" className="logo" />
            </div>
        </div>
    );
};

export default LoadingPage;
