import React, { useEffect, useRef, useState } from 'react';
import Lottie from 'react-lottie';
import './LoadingPage.css'; // Import your CSS for styling
import muteUnmuteData from './mute_unmute.json'; // Adjust the path based on your folder structure

const LoadingPage = () => {
    const videoRef = useRef(null);
    const audioRef = useRef(null);
    const [displayedMessage, setDisplayedMessage] = useState('');
    const intervalRef = useRef(null);
    const [isMuted, setIsMuted] = useState(true); // Start with mute

    const fullMessage = "Step Back in Time: Explore History with Us!";

    useEffect(() => {
        // Set video playback rate
        if (videoRef.current) {
            videoRef.current.playbackRate = 0.5;
        }

        // Function to display the message one letter at a time
        const displayMessage = (message) => {
            let index = 0;
            setDisplayedMessage('');

            intervalRef.current = setInterval(() => {
                if (index < message.length) {
                    setDisplayedMessage((prev) => prev + message[index]);
                    index++;
                } else {
                    clearInterval(intervalRef.current);
                }
            }, 150);
        };

        // Start displaying the message
        displayMessage(fullMessage);

        // Cleanup the interval on component unmount
        return () => clearInterval(intervalRef.current);
    }, []);

    // Function to toggle mute
    const toggleMute = () => {
        setIsMuted(!isMuted);
        if (audioRef.current) {
            if (isMuted) {
                audioRef.current.play();
            } else {
                audioRef.current.pause();
            }
        }
    };

    return (
        <div className="loading-container">
            <video
                ref={videoRef}
                autoPlay
                loop
                muted={isMuted}
                className="video-background"
            >
                <source src="/loadingBG2.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <audio ref={audioRef} loop>
                <source src="/landing_speech.mp3" type="audio/mp3" />
                Your browser does not support the audio tag.
            </audio>

            <div className="welcome-message">
                {displayedMessage}
            </div>

            <div className="logo-container">
                <img src="/logo1.png" alt="Time Machine Logo" className="logo" />
            </div>

            {/* Mute/Unmute Button */}
            <div className="mute-unmute-icon" onClick={toggleMute}>
                <Lottie 
                    options={{
                        loop: false,
                        autoplay: false, // Do not autoplay on load
                        animationData: muteUnmuteData,
                        rendererSettings: {
                            preserveAspectRatio: 'xMidYMid slice'
                        }
                    }}
                    height={50}
                    width={50}
                    isStopped={isMuted} // Stop animation when muted
                />
            </div>
        </div>
    );
};

export default LoadingPage;
